          buffer text = '';
          chunks integer;
          chunk integer = 0;
      begin
          chunks = (value ->> ('__bufchunks_' || name))::integer;
          loop
              exit when chunks is null or chunk >= chunks;
              buffer = buffer || (value ->> ('__buf' || chunk || '_' || name))::text;
              chunk = chunk + 1;
          end loop;
          return convert_from(decode(buffer, 'base64'), 'utf8');
      end;
  $$
  language plpgSQL
  strict immutable;

