            ('{__bufchunks_' || name || '}')::text[],
            to_jsonb(1));
        bytes = convert_to(data, 'utf8');
        value = jsonb_set(value,
            ('{__buf0_' || name || '}')::text[],
            to_jsonb(replace(encode(bytes, 'base64'), E'\n', '')));
