      if in_str = '!' then
        return '';
      end if;

      with str as (
        select
          -- `plain` is an array of all non-encoded substrings, including a blank first string
          -- if necessary so that we can assume the first component is blank
          case when in_str ~ '^![0-9a-fA-F][0-9a-fA-F]'
